CREATE DATABASE IF NOT EXISTS foodnet;
USE foodnet;

CREATE TABLE IF NOT EXISTS Clientes (
    id_cliente INT PRIMARY KEY AUTO_INCREMENT,
    cpf VARCHAR(14) UNIQUE NOT NULL,
    nome VARCHAR(70) NOT NULL,
    data_nascimento DATE,
    email VARCHAR(50) UNIQUE NOT NULL,
    telefone VARCHAR(20),
    data_cadastro DATETIME NOT NULL
);

CREATE TABLE IF NOT EXISTS Enderecos (
    id_endereco INT PRIMARY KEY AUTO_INCREMENT,
    id_cliente INT NOT NULL,
    rua VARCHAR(100) NOT NULL,
    numero VARCHAR(10) NOT NULL,
    bairro VARCHAR(50),
    cidade VARCHAR(50) NOT NULL,
    estado VARCHAR(2) NOT NULL,
    cep VARCHAR(9) NOT NULL,
    tipo ENUM('Casa', 'Trabalho', 'Outro'),
    FOREIGN KEY (id_cliente) REFERENCES Clientes(id_cliente) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS Restaurantes (
    id_restaurante INT PRIMARY KEY AUTO_INCREMENT,
    cnpj VARCHAR(18) UNIQUE NOT NULL,
    nome VARCHAR(70) NOT NULL,
    endereco VARCHAR(100) NOT NULL,
    categoria VARCHAR(50) NOT NULL,
    horario_funcionamento VARCHAR(100),
    nota_media DECIMAL(3, 2) DEFAULT 0.00
);

CREATE TABLE IF NOT EXISTS Produtos (
    id_produto INT PRIMARY KEY AUTO_INCREMENT,
    id_restaurante INT NOT NULL,
    nome VARCHAR(70) NOT NULL,
    descricao TEXT,
    categoria VARCHAR(50) NOT NULL,
    preco DECIMAL(10, 2) NOT NULL,
    disponibilidade BOOLEAN NOT NULL DEFAULT TRUE,
    FOREIGN KEY (id_restaurante) REFERENCES Restaurantes(id_restaurante) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS Entregadores (
    id_entregador INT PRIMARY KEY AUTO_INCREMENT,
    nome VARCHAR(70) NOT NULL,
    cpf VARCHAR(14) UNIQUE NOT NULL,
    telefone VARCHAR(20) NOT NULL,
    placa_veiculo VARCHAR(10) UNIQUE NOT NULL
);

CREATE TABLE IF NOT EXISTS Cupons (
    id_cupom INT PRIMARY KEY AUTO_INCREMENT,
    id_restaurante INT NOT NULL,
    codigo VARCHAR(50) UNIQUE NOT NULL,
    valor_desconto DECIMAL(10, 2) NOT NULL,
    data_validade DATE NOT NULL,
    FOREIGN KEY (id_restaurante) REFERENCES Restaurantes(id_restaurante) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS Pedidos (
    id_pedido INT PRIMARY KEY AUTO_INCREMENT,
    id_cliente INT NOT NULL,
    id_restaurante INT NOT NULL,
    id_entregador INT,
    id_cupom INT,
    data_hora DATETIME NOT NULL,
    forma_pagamento VARCHAR(50) NOT NULL,
    status_pedido ENUM('Em preparação', 'A caminho', 'Entregue', 'Cancelado') NOT NULL,
    FOREIGN KEY (id_cliente) REFERENCES Clientes(id_cliente) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (id_restaurante) REFERENCES Restaurantes(id_restaurante) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (id_entregador) REFERENCES Entregadores(id_entregador) ON DELETE SET NULL ON UPDATE CASCADE,
    FOREIGN KEY (id_cupom) REFERENCES Cupons(id_cupom) ON DELETE SET NULL ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS ItensPedido (
    id_item_pedido INT PRIMARY KEY AUTO_INCREMENT,
    id_pedido INT NOT NULL,
    id_produto INT NOT NULL,
    quantidade INT NOT NULL,
    preco_unitario DECIMAL(10, 2) NOT NULL,
    FOREIGN KEY (id_pedido) REFERENCES Pedidos(id_pedido) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (id_produto) REFERENCES Produtos(id_produto) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS Avaliacoes (
    id_avaliacao INT PRIMARY KEY AUTO_INCREMENT,
    id_pedido INT NOT NULL,
    id_cliente INT NOT NULL,
    nota INT NOT NULL CHECK (nota >= 1 AND nota <= 5),
    comentario TEXT,
    data_avaliacao DATETIME NOT NULL,
    FOREIGN KEY (id_pedido) REFERENCES Pedidos(id_pedido) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (id_cliente) REFERENCES Clientes(id_cliente) ON DELETE CASCADE ON UPDATE CASCADE
);

